import React, { useEffect, useState } from "react";
import axios from "axios";

function OrganizerDashboard() {

  const [eventId] = useState(1);
  const [bookings, setBookings] = useState([]);
  const [totalBookings, setTotalBookings] = useState(0);
  const [totalRevenue, setTotalRevenue] = useState(0);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    const bookingsRes = await axios.get(`http://localhost:8080/api/bookings/event/${eventId}`);
    const totalRes = await axios.get(`http://localhost:8080/api/bookings/total/${eventId}`);
    const revenueRes = await axios.get(`http://localhost:8080/api/bookings/revenue/${eventId}`);

    setBookings(bookingsRes.data);
    setTotalBookings(totalRes.data);
    setTotalRevenue(revenueRes.data);
  };

  return (
    <div>
      <h2>Organizer Dashboard</h2>

      <h3>Total Bookings: {totalBookings}</h3>
      <h3>Total Revenue: â‚¹{totalRevenue}</h3>

      <h3>Booking Details</h3>
      <table border="1">
        <thead>
          <tr>
            <th>Booking ID</th>
            <th>User ID</th>
            <th>Seats Booked</th>
            <th>Total Amount</th>
            <th>Booking Date</th>
          </tr>
        </thead>
        <tbody>
  {bookings.map((booking) => (
    <tr key={booking.id}>
      <td>{booking.id}</td>
      <td>{booking.event?.id}</td>
      <td>{booking.seatsBooked}</td>
      <td>{booking.totalAmount}</td>
      <td>{booking.bookingDate}</td>
    </tr>
  ))}
</tbody>

      </table>
    </div>
  );
}

export default OrganizerDashboard;
