import "../styles/Dashboard.css";
import React, { useEffect, useState } from "react";
import axios from "axios";

import {
  Chart as ChartJS,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend
} from "chart.js";
import { Bar } from "react-chartjs-2";

ChartJS.register(BarElement, CategoryScale, LinearScale, Tooltip, Legend);

function OrganizerDashboard() {
  const [bookings, setBookings] = useState([]);
  const [filter, setFilter] = useState("total");

  const fetchData = async () => {
    try {
      const bookingsRes = await axios.get(`http://localhost:8080/api/bookings`);
      setBookings(bookingsRes.data);
    } catch (error) {
      console.error("Error fetching dashboard data", error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // Aggregate bookings per event
  const eventMap = {};
  bookings.forEach(b => {
    const eventId = b.eventId ?? "N/A";
    if (!eventMap[eventId]) {
      eventMap[eventId] = { count: 0, revenue: 0, seats: 0 };
    }
    eventMap[eventId].count += 1;
    eventMap[eventId].revenue += b.totalAmount;
    eventMap[eventId].seats += b.seatsBooked;
  });

  const events = Object.keys(eventMap);
  const bookingCounts = events.map(e => eventMap[e].count);
  const revenueData = events.map(e =>
    filter === "total" ? eventMap[e].revenue : eventMap[e].revenue * 2
  );
  const seatsData = events.map(e =>
    filter === "total" ? eventMap[e].seats : eventMap[e].seats * 2
  );

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Organizer Dashboard</h1>
      </div>

      {/* Filter */}
      <div style={{ marginBottom: "20px" }}>
        <label>
          Show:
          <select
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
            style={{ marginLeft: "10px" }}
          >
            <option value="total">Total</option>
            <option value="projected">Projected</option>
          </select>
        </label>
      </div>

      {/* Charts */}
      <div className="charts-row">
        <div className="chart-container">
          <h3>Bookings Count per Event</h3>
          <Bar
            data={{
              labels: events,
              datasets: [
                {
                  label: "Bookings Count",
                  data: bookingCounts,
                  backgroundColor: "#4f46e5"
                }
              ]
            }}
            options={{ responsive: true, maintainAspectRatio: false }}
          />
        </div>

        <div className="chart-container">
          <h3>Revenue per Event</h3>
          <Bar
            data={{
              labels: events,
              datasets: [
                {
                  label: "Revenue (₹)",
                  data: revenueData,
                  backgroundColor: "#f97316"
                }
              ]
            }}
            options={{ responsive: true, maintainAspectRatio: false }}
          />
        </div>

        <div className="chart-container">
          <h3>Seats Booked per Event</h3>
          <Bar
            data={{
              labels: events,
              datasets: [
                {
                  label: "Seats Booked",
                  data: seatsData,
                  backgroundColor: "#10b981"
                }
              ]
            }}
            options={{ responsive: true, maintainAspectRatio: false }}
          />
        </div>
      </div>
    </div>
  );
}

export default OrganizerDashboard;
