import "../styles/Dashboard.css";
import React, { useEffect, useState } from "react";
import axios from "axios";

import {
  Chart as ChartJS,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend
} from "chart.js";
import { Bar } from "react-chartjs-2";

ChartJS.register(BarElement, CategoryScale, LinearScale, Tooltip, Legend);

function OrganizerDashboard() {
  const [eventId] = useState(1);
  const [bookings, setBookings] = useState([]);
  const [totalBookings, setTotalBookings] = useState(0);
  const [totalRevenue, setTotalRevenue] = useState(0);
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const bookingsRes = await axios.get(
        `http://localhost:8080/api/bookings/event/${eventId}`
      );
      const totalRes = await axios.get(
        `http://localhost:8080/api/bookings/total/${eventId}`
      );
      const revenueRes = await axios.get(
        `http://localhost:8080/api/bookings/revenue/${eventId}`
      );

      setBookings(bookingsRes.data);
      setTotalBookings(totalRes.data);
      setTotalRevenue(revenueRes.data);
    } catch (error) {
      console.error("Error fetching dashboard data", error);
    }
  };

  return (
    <div className={`dashboard-container ${darkMode ? "dark" : "light"}`}>
      
      {/* Header */}
      <div className="dashboard-header">
        <h1>Organizer Dashboard</h1>
        <button
          className="toggle-btn"
          onClick={() => setDarkMode(!darkMode)}
        >
          {darkMode ? "Light Mode â˜€" : "Dark Mode ðŸŒ™"}
        </button>
      </div>

      {/* Summary Cards */}
      <div className="summary-container">
        <div className="summary-card">
          <h3>Total Bookings</h3>
          <p>{totalBookings}</p>
        </div>

        <div className="summary-card">
          <h3>Total Revenue</h3>
          <p>â‚¹{totalRevenue}</p>
        </div>
      </div>

      {/* Revenue Chart */}
      <div style={{ maxWidth: "600px", margin: "40px auto" }}>
        <Bar
          data={{
            labels: ["Total Revenue"],
            datasets: [
              {
                label: "Revenue",
                data: [totalRevenue],
                backgroundColor: "#4f46e5"
              }
            ]
          }}
        />
      </div>

      {/* Booking Table */}
      <h2>Booking Details</h2>
      <table className="booking-table">
        <thead>
          <tr>
            <th>Booking ID</th>
            <th>Event ID</th>
            <th>Seats Booked</th>
            <th>Total Amount</th>
            <th>Booking Date</th>
          </tr>
        </thead>
        
      </table>
    </div>
  );
}

export default OrganizerDashboard;
