import React, { useState, useEffect } from "react";
import "../styles/Payment.css";

const PaymentModal = ({ bookingData, onClose }) => {

  const [loading, setLoading] = useState(false);
  const [method, setMethod] = useState("CARD");
  const [paymentResult, setPaymentResult] = useState(null);

  // CARD fields
  const [cardNumber, setCardNumber] = useState("");
  const [cardName, setCardName] = useState("");
  const [expiry, setExpiry] = useState("");
  const [cvv, setCvv] = useState("");

  // UPI fields
  const [upiId, setUpiId] = useState("");

  // PAYPAL fields
  const [paypalEmail, setPaypalEmail] = useState("");

  const loggedUser = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    document.body.style.overflow = "hidden";
    return () => (document.body.style.overflow = "auto");
  }, []);

  const validateFields = () => {
    if (method === "CARD") {
      if (!cardNumber || cardNumber.replace(/\s/g, "").length !== 16) {
        alert("Please enter a valid 16-digit card number");
        return false;
      }
      if (!cardName) {
        alert("Please enter the card holder name");
        return false;
      }
      if (!expiry) {
        alert("Please enter expiry date");
        return false;
      }
      if (!cvv || cvv.length < 3) {
        alert("Please enter a valid CVV");
        return false;
      }
    }
    if (method === "UPI") {
      if (!upiId || !upiId.includes("@")) {
        alert("Please enter a valid UPI ID (e.g. name@upi)");
        return false;
      }
    }
    if (method === "PAYPAL") {
      if (!paypalEmail || !paypalEmail.includes("@")) {
        alert("Please enter a valid PayPal email");
        return false;
      }
    }
    return true;
  };

  const formatCardNumber = (value) => {
    const v = value.replace(/\D/g, "").substring(0, 16);
    return v.replace(/(.{4})/g, "$1 ").trim();
  };

  const payNow = async () => {
    if (!validateFields()) return;

    setLoading(true);

    const paymentData = {
      bookingId: bookingData.bookingId,
      eventId: bookingData.eventId,
      userId: loggedUser.id,
      paymentMethod: method,
      amount: bookingData.amount,
      cardNumber: method === "CARD" ? cardNumber.replace(/\s/g, "") : null,
      cardName: method === "CARD" ? cardName : null,
      expiry: method === "CARD" ? expiry : null,
      upiId: method === "UPI" ? upiId : null,
      paypalEmail: method === "PAYPAL" ? paypalEmail : null,
    };

    try {
      const res = await fetch(
        "http://localhost:8080/api/payment/process",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(paymentData)
        }
      );

      const data = await res.json();
      setPaymentResult(data);

    } catch (err) {
      alert("Payment failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="payment-modal-overlay">
      <div className="success-card" style={{ width: "460px", maxHeight: "90vh", overflowY: "auto" }}>

        {!paymentResult ? (
          <>
            <h2>Complete Payment</h2>
            <p style={{ fontSize: "20px", fontWeight: "bold", color: "#7c3aed", marginBottom: "20px" }}>
              ‚Çπ{bookingData.amount}
            </p>

            {/* Payment Method Selector */}
            <div className="method-group">
              {["CARD", "UPI", "PAYPAL"].map(m => (
                <button
                  key={m}
                  className={`method-btn ${method === m ? "active" : ""}`}
                  onClick={() => setMethod(m)}
                >
                  {m === "CARD" && "üí≥ "}
                  {m === "UPI" && "üì± "}
                  {m === "PAYPAL" && "üÖøÔ∏è "}
                  {m}
                </button>
              ))}
            </div>

            {/* CARD FIELDS */}
            {method === "CARD" && (
              <div className="payment-fields">
                <label>Card Number</label>
                <input
                  type="text"
                  placeholder="1234 5678 9012 3456"
                  value={cardNumber}
                  maxLength={19}
                  onChange={(e) => setCardNumber(formatCardNumber(e.target.value))}
                />

                <label>Card Holder Name</label>
                <input
                  type="text"
                  placeholder="Name on card"
                  value={cardName}
                  onChange={(e) => setCardName(e.target.value)}
                />

                <div style={{ display: "flex", gap: "12px" }}>
                  <div style={{ flex: 1 }}>
                    <label>Expiry Date</label>
                    <input
                      type="month"
                      value={expiry}
                      onChange={(e) => setExpiry(e.target.value)}
                    />
                  </div>
                  <div style={{ flex: 1 }}>
                    <label>CVV</label>
                    <input
                      type="password"
                      placeholder="***"
                      maxLength={4}
                      value={cvv}
                      onChange={(e) => setCvv(e.target.value.replace(/\D/g, ""))}
                    />
                  </div>
                </div>
              </div>
            )}

            {/* UPI FIELDS */}
            {method === "UPI" && (
              <div className="payment-fields">
                <label>UPI ID</label>
                <input
                  type="text"
                  placeholder="yourname@upi"
                  value={upiId}
                  onChange={(e) => setUpiId(e.target.value)}
                />
                <p style={{ fontSize: "12px", color: "#6b7280", marginTop: "6px" }}>
                  Enter your UPI ID linked to your bank account
                </p>
              </div>
            )}

            {/* PAYPAL FIELDS */}
            {method === "PAYPAL" && (
              <div className="payment-fields">
                <label>PayPal Email</label>
                <input
                  type="email"
                  placeholder="yourname@paypal.com"
                  value={paypalEmail}
                  onChange={(e) => setPaypalEmail(e.target.value)}
                />
                <p style={{ fontSize: "12px", color: "#6b7280", marginTop: "6px" }}>
                  Enter the email address linked to your PayPal account
                </p>
              </div>
            )}

            <button
              className="pay-btn"
              onClick={payNow}
              disabled={loading}
              style={{ marginTop: "20px" }}
            >
              {loading ? "Processing..." : `Pay ‚Çπ${bookingData.amount}`}
            </button>

            <button
              onClick={onClose}
              style={{
                marginTop: "10px",
                width: "100%",
                padding: "10px",
                background: "transparent",
                border: "1px solid #e5e7eb",
                borderRadius: "10px",
                cursor: "pointer",
                color: "#6b7280"
              }}
            >
              Cancel
            </button>
          </>
        ) : (
          <>
            <div style={{ fontSize: "50px", marginBottom: "10px" }}>‚úÖ</div>
            <h2 style={{ color: "#16a34a" }}>Payment Successful!</h2>

            <div style={{
              background: "#f0fdf4",
              border: "1px solid #bbf7d0",
              borderRadius: "12px",
              padding: "15px",
              marginBottom: "15px",
              textAlign: "left"
            }}>
              <p><strong>Transaction ID:</strong> {paymentResult.transactionId}</p>
              <p><strong>Amount Paid:</strong> ‚Çπ{paymentResult.amount}</p>
              <p><strong>Payment Method:</strong> {method}</p>
              {method === "CARD" && <p><strong>Card:</strong> **** **** **** {cardNumber.slice(-4)}</p>}
              {method === "UPI" && <p><strong>UPI ID:</strong> {upiId}</p>}
              {method === "PAYPAL" && <p><strong>PayPal:</strong> {paypalEmail}</p>}
            </div>

            <p style={{ color: "#6b7280", fontSize: "13px" }}>Scan QR code at the venue</p>
            <img
              className="qr-image"
              src={`http://localhost:8080/qrcodes/${paymentResult.transactionId}.png`}
              alt="QR Code"
            />

            <button className="pay-btn" onClick={onClose} style={{ marginTop: "15px" }}>
              Done üéâ
            </button>
          </>
        )}

      </div>
    </div>
  );
};

export default PaymentModal;
